import {AfterViewInit, Component, ElementRef, QueryList, ViewChild, ViewChildren} from '@angular/core';
import {COURSES} from '../db-data';
import {Course} from './model/course';
import {CourseCardComponent} from './course-card/course-card.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements AfterViewInit {

    courses = [...COURSES];
    // courses = COURSES;


    @ViewChildren(CourseCardComponent, {read: ElementRef})
    cards : QueryList<ElementRef>;


    constructor() {

    }

    ngAfterViewInit() {

    }

    onCourseSelected(course:Course) {

    }

}
<div class="top-menu">

    <img class="logo"
         src="https://angular-academy.s3.amazonaws.com/main-logo/main-page-logo-small-hat.png">

</div>



<div class="courses">
  @for (course of courses; track course.id; let index = $index; let count = $count; let first = $first; let last = $last; let odd = $odd; let even = $even ){


    <h3> total courses {{count}}</h3>
    <course-card
            [index] = "index"
            [count] = "count"
            [first] = "first"
            [last] = "last"

            [class.is-even] = "even"
            [class.is-odd] = "odd"
            (courseSelected)="onCourseSelected($event)"
                 [course]="course">

        <course-image [src]="course.iconUrl"></course-image>

        <div class="course-description">
            {{ course.longDescription }}
        </div>

    </course-card>
  }

  @empty {
    <h1>No courses found! </h1>
  }


</div>
