import {
    AfterContentInit,
    AfterViewInit,
    Component,
    ContentChild,
    ContentChildren,
    ElementRef,
    EventEmitter,
    Input,
    OnInit,
    Output, QueryList, TemplateRef,
    ViewChild
} from '@angular/core';
import {COURSES} from '../../db-data';
import {Course} from '../model/course';
import {CourseImageComponent} from '../course-image/course-image.component';

@Component({
    selector: 'course-card',
    templateUrl: './course-card.component.html',
    styleUrls: ['./course-card.component.css']
})
export class CourseCardComponent implements OnInit, AfterViewInit, AfterContentInit {

    @Input()
    course: Course;

    @Input()
    cardIndex: number;

    @Input() index: number;

    @Input() count: number;
    @Input() first: any;
    @Input() last: any; 

    @Output('courseSelected')
    courseEmitter = new EventEmitter<Course>();

    @ContentChildren(CourseImageComponent, {read: ElementRef})
    images: QueryList<ElementRef>;



    constructor() {

    }

    ngAfterViewInit() {

    }

    ngAfterContentInit() {

    }

    ngOnInit() {
        console.log(this.first, this.last);

    }

    isImageVisible() {
        return this.course && this.course.iconUrl;
    }

    onCourseViewed() {

        this.courseEmitter.emit(this.course);

    }

    cardClasses() {
        if (this.course.category == 'BEGINNER') {
            return 'beginner';
        }
    }

    cardStyles() {
        return {
            'background-image': 'url(' + this.course.iconUrl + ')'

        };
    }



}

<h4 style="margin-bottom: 20px;">Total courses {{count}} </h4>

<h2>{{first}}</h2>
<div class="course-card" *ngIf="course" #container>
    <div class="course-title">
       {{index }} {{ cardIndex || '' + ' ' + course.description }}
    </div>

    @if(course.iconUrl){
        <!-- <ng-content select="course-image"></ng-content> -->
        <img [src]="course.iconUrl" alt="img">
    }

    <!-- <ng-content select="course-image"
                *ngIf="course.iconUrl; else noImage"></ng-content> -->

    <!-- <ng-template #noImage>

        <ng-container
                *ngTemplateOutlet="noImageTpl;context: {description:course.description}">

        </ng-container>

    </ng-template> -->

    <ng-content select=".course-description"></ng-content>

    <div class="course-category" [ngSwitch]="course.category">

        <div class="category" *ngSwitchCase="'BEGINNER'">Beginner</div>

        <div class="category" *ngSwitchCase="'INTERMEDIATE'">Intermediate</div>

        <div class="category" *ngSwitchCase="'ADVANCED'">Advanced</div>

    </div>

    <button (click)="onCourseViewed()">View Course</button>

</div>
